Ext.define('iouLab.store.editLabs', {
    extend: 'Ext.data.Store',

    constructor: function(cfg) {
        var me = this;
        cfg = cfg || {};
        me.callParent([Ext.apply({
            autoLoad: true,
            autoSync: true,
            storeId: 'editLabs',
            fields: [
                {
                    name: 'id'
                },
                {
                    name: 'name'
                },
                {
                    name: 'description'
                }
            ],
            proxy: {
                type: 'rest',
                url: '/rest/labs'
            },
            listeners: {
                datachanged: {
                    fn: me.onStoreDataChangeD,
                    scope: me
                }
            }
        }, cfg)]);
    },

    onStoreDataChangeD: function(store, eOpts) {
        //console.log('data changed');
        var s=Ext.getCmp('treeMenu').getStore();
        var labs=s.getById('labs');
        labs.removeAll();
        store.each(function(n) {
            labs.appendChild({
                id: n.get('id'),
                leaf: true,
                text: n.get('name')
            });
        });
        
    }

});