Ext.define('iouLab.view.deviceName', {
    extend: 'Ext.window.Window',
    alias: 'widget.deviceName',

    width: 400,
    title: 'Device',
    modal: true,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'fieldset',
                    title: 'Properties',
                    items: [
                        {
                            xtype: 'textfield',
                            anchor: '100%',
                            itemId: 'name',
                            fieldLabel: 'Name',
                            labelAlign: 'right'
                        }
                    ]
                }
            ],
            dockedItems: [
                {
                    xtype: 'toolbar',
                    dock: 'bottom',
                    items: [
                        {
                            xtype: 'tbfill'
                        },
                        {
                            xtype: 'button',
                            text: 'Save',
                            listeners: {
                                click: {
                                    fn: me.onButtonClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                }
            ],
            listeners: {
                show: {
                    fn: me.onWindowShow,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onButtonClick: function(button, e, eOpts) {
        var comp = button.up('window');
        console.log('save',comp,arguments);
        
        Ext.Ajax.request({
            method: 'PUT',
            url: '/rest/device/'+comp.deviceId,
            jsonData: Ext.JSON.encode({
                name: comp.down('#name').value
            }),
            success: function(res) {
                comp.close();
            }
        });
        
    },

    onWindowShow: function(component, eOpts) {
        console.log('Window show',arguments,component);
        
        Ext.Ajax.request({
            url: '/rest/device/'+component.deviceId,
            success: function(res) {
                var obj = Ext.JSON.decode(res.responseText);
                component.down('#name').setValue(obj.name);
            }
        });
        
        
    }

});